(4987, 56, 8, 'فَأَصْحَابُ الْمَيْمَنَةِ مَا أَصْحَابُ الْمَيْمَنَةِ', NULL, NULL, NULL, NULL),
(4988, 56, 9, 'وَأَصْحَابُ الْمَشْأَمَةِ مَا أَصْحَابُ الْمَشْأَمَةِ', NULL, NULL, NULL, NULL),
(4989, 56, 10, 'وَالسَّابِقُونَ السَّابِقُونَ', NULL, NULL, NULL, NULL),
(4990, 56, 11, 'أُولَٰئِكَ الْمُقَرَّبُونَ', NULL, NULL, NULL, NULL),
(4991, 56, 12, 'فِي جَنَّاتِ النَّعِيمِ', NULL, NULL, NULL, NULL),
(4992, 56, 13, 'ثُلَّةٌ مِنَ الْأَوَّلِينَ', NULL, NULL, NULL, NULL),
(4993, 56, 14, 'وَقَلِيلٌ مِنَ الْآخِرِينَ', NULL, NULL, NULL, NULL),
