(5009, 56, 30, 'وَظِلٍّ مَمْدُودٍ', NULL, NULL, NULL, NULL),
(5010, 56, 31, 'وَمَاءٍ مَسْكُوبٍ', NULL, NULL, NULL, NULL),
(5011, 56, 32, 'وَفَاكِهَةٍ كَثِيرَةٍ', NULL, NULL, NULL, NULL),
(5012, 56, 33, 'لَا مَقْطُوعَةٍ وَلَا مَمْنُوعَةٍ', NULL, NULL, NULL, NULL),
(5013, 56, 34, 'وَفُرُشٍ مَرْفُوعَةٍ', NULL, NULL, NULL, NULL),
(5014, 56, 35, 'إِنَّا أَنْشَأْنَاهُنَّ إِنْشَاءً', NULL, NULL, NULL, NULL),
(5015, 56, 36, 'فَجَعَلْنَاهُنَّ أَبْكَارًا', NULL, NULL, NULL, NULL),
(5016, 56, 37, 'عُرُبًا أَتْرَابًا', NULL, NULL, NULL, NULL),
(5017, 56, 38, 'لِأَصْحَابِ الْيَمِينِ', NULL, NULL, NULL, NULL),
(5018, 56, 39, 'ثُلَّةٌ مِنَ الْأَوَّلِينَ', NULL, NULL, NULL, NULL),
(5019, 56, 40, 'وَثُلَّةٌ مِنَ الْآخِرِينَ', NULL, NULL, NULL, NULL),
(5020, 56, 41, 'وَأَصْحَابُ الشِّمَالِ مَا أَصْحَابُ الشِّمَالِ', NULL, NULL, NULL, NULL),
(5021, 56, 42, 'فِي سَمُومٍ وَحَمِيمٍ', NULL, NULL, NULL, NULL),
