(6201, 107, 4, 'فَوَيْلٌ لِلْمُصَلِّينَ', NULL, NULL, NULL, NULL),
(6202, 107, 5, 'الَّذِينَ هُمْ عَنْ صَلَاتِهِمْ سَاهُونَ', NULL, NULL, NULL, NULL),
(6203, 107, 6, 'الَّذِينَ هُمْ يُرَاءُونَ', NULL, NULL, NULL, NULL),
(6204, 107, 7, 'وَيَمْنَعُونَ الْمَاعُونَ', NULL, NULL, NULL, NULL),
(6205, 108, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا أَعْطَيْنَاكَ الْكَوْثَرَ', NULL, NULL, NULL, NULL),
(6206, 108, 2, 'فَصَلِّ لِرَبِّكَ وَانْحَرْ', NULL, NULL, NULL, NULL),
(6207, 108, 3, 'إِنَّ شَانِئَكَ هُوَ الْأَبْتَرُ', NULL, NULL, NULL, NULL),
(6208, 109, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ يَا أَيُّهَا الْكَافِرُونَ', NULL, NULL, NULL, NULL),
(6209, 109, 2, 'لَا أَعْبُدُ مَا تَعْبُدُونَ', NULL, NULL, NULL, NULL),
(6210, 109, 3, 'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ', NULL, NULL, NULL, NULL),
(6211, 109, 4, 'وَلَا أَنَا عَابِدٌ مَا عَبَدْتُمْ', NULL, NULL, NULL, NULL),
(6212, 109, 5, 'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ', NULL, NULL, NULL, NULL),
(6213, 109, 6, 'لَكُمْ دِينُكُمْ وَلِيَ دِينِ', NULL, NULL, NULL, NULL),
(6214, 110, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ', NULL, NULL, NULL, NULL),
(6215, 110, 2, 'وَرَأَيْتَ النَّاسَ يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْوَاجًا', NULL, NULL, NULL, NULL),
(6216, 110, 3, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُ ۚ إِنَّهُ كَانَ تَوَّابًا', NULL, NULL, NULL, NULL),
(6217, 111, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَّتْ يَدَا أَبِي لَهَبٍ وَتَبَّ', NULL, NULL, NULL, NULL),
(6218, 111, 2, 'مَا أَغْنَىٰ عَنْهُ مَالُهُ وَمَا كَسَبَ', NULL, NULL, NULL, NULL),
(6219, 111, 3, 'سَيَصْلَىٰ نَارًا ذَاتَ لَهَبٍ', NULL, NULL, NULL, NULL),
(6220, 111, 4, 'وَامْرَأَتُهُ حَمَّالَةَ الْحَطَبِ', NULL, NULL, NULL, NULL),
(6221, 111, 5, 'فِي جِيدِهَا حَبْلٌ مِنْ مَسَدٍ', NULL, NULL, NULL, NULL),
(6222, 112, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ هُوَ اللَّهُ أَحَدٌ', NULL, NULL, NULL, NULL),
(6223, 112, 2, 'اللَّهُ الصَّمَدُ', NULL, NULL, NULL, NULL),
(6224, 112, 3, 'لَمْ يَلِدْ وَلَمْ يُولَدْ', NULL, NULL, NULL, NULL),
(6225, 112, 4, 'وَلَمْ يَكُنْ لَهُ كُفُوًا أَحَدٌ', NULL, NULL, NULL, NULL),
(6226, 113, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ', NULL, NULL, NULL, NULL),
(6227, 113, 2, 'مِنْ شَرِّ مَا خَلَقَ', NULL, NULL, NULL, NULL),
(6228, 113, 3, 'وَمِنْ شَرِّ غَاسِقٍ إِذَا وَقَبَ', NULL, NULL, NULL, NULL),
(6229, 113, 4, 'وَمِنْ شَرِّ النَّفَّاثَاتِ فِي الْعُقَدِ', NULL, NULL, NULL, NULL),
(6230, 113, 5, 'وَمِنْ شَرِّ حَاسِدٍ إِذَا حَسَدَ', NULL, NULL, NULL, NULL),
(6231, 114, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ النَّاسِ', NULL, NULL, NULL, NULL),
(6232, 114, 2, 'مَلِكِ النَّاسِ', NULL, NULL, NULL, NULL),
(6233, 114, 3, 'إِلَٰهِ النَّاسِ', NULL, NULL, NULL, NULL),
(6234, 114, 4, 'مِنْ شَرِّ الْوَسْوَاسِ الْخَنَّاسِ', NULL, NULL, NULL, NULL),
(6235, 114, 5, 'الَّذِي يُوَسْوِسُ فِي صُدُورِ النَّاسِ', NULL, NULL, NULL, NULL),
(6236, 114, 6, 'مِنَ الْجِنَّةِ وَالنَّاسِ', NULL, NULL, NULL, NULL);
